import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { User } from 'src/app/model/user';
import { AlertifyService } from 'src/app/services/alertify.service';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.scss']
})
export class UserLoginComponent implements OnInit {

  constructor(private authService: AuthService,
              private alertify: AlertifyService,
              private router: Router ) { }

  ngOnInit() {

    }

    onLogin(loginForm: NgForm){
    console.log('loginForm.value', loginForm.value);
   // const.log(loginForm.value)

    const token = this.authService.authUser(loginForm.value);
    console.log(' user ', token);
    // user indefined !!
    if (token) {
      localStorage.setItem('token', token.userName);
      this.alertify.success('Login Successful');
      console.log('login Successuful');
      this.router.navigate(['/']);
     }
      else {
       console.log('login not Successful');
       this.alertify.error('User id or password is wrong');

      }
     }
    }

<div class="row">
  <div class="col-6 m-auto">
    <div class="card m-auto">
      <div class="card-header">
       Login
      </div>
      <div class="card-body">

        <form #loginForm='ngForm' (ngSubmit)="onLogin(loginForm)">
          <div class="form-group col-12">
            <label>Name</label>
            <input class="form-control" required  ngModel name="userName">
          </div>

          <div class="form-group col-12">
            <label>Password</label>
            <input class="form-control" required type="password" ngModel name="password">
          </div>

          <div class="form-group col-12">
            <button [disabled]="!loginForm.valid" type="submit" class="btn btn-primary">Login</button>
            <button type="button" class="btn btn-secondary ml-2">Cancel</button>

          </div>
        </form>
      </div>
    </div>
  </div>
</div>
